import React from "react";
import { Col, Container, Row } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEnvelope, faPhone, faMapMarkerAlt } from "@fortawesome/free-solid-svg-icons";
import {
  faFacebook,
  faTwitter,
  faInstagram,
  faLinkedin,
} from "@fortawesome/free-brands-svg-icons";

class Footer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      height: 10,
    };
    this.resizeHandler = this.resizeHandler.bind(this);
  }

  resizeHandler() {
    const height = this.header.clientHeight;
    this.setState({ height });
    this.props.onHeightChange(height);
  }

  componentDidMount() {
    this.resizeHandler();
    window.addEventListener("resize", this.resizeHandler);
  }

  componentWillUnmount() {
    window.removeEventListener("resize", this.resizeHandler);
  }

  render() {
    return (
      <Container
        fluid
        className="fixed-bottom bg-dark text-light py-4"
        style={{ borderTop: "2px solid #fff" }}
        ref={(footer) => {
          this.header = footer;
        }}
      >
        <Row className="justify-content-center">
          <Col xs={12} sm={6} md={4} className="text-center mb-4">
            <h5>About Us</h5>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla ultrices nisi et gravida tincidunt.</p>
          </Col>
          <Col xs={12} sm={6} md={4} className="text-center mb-4">
            <h5>Contact Us</h5>
            <p><FontAwesomeIcon icon={faEnvelope} className="me-2"/>info@example.com</p>
            <p><FontAwesomeIcon icon={faPhone} className="me-2"/>+1234567890</p>
            <p><FontAwesomeIcon icon={faMapMarkerAlt} className="me-2"/>123 Street, City</p>
          </Col>
          <Col xs={12} className="text-center">
            <h5>Follow Us</h5>
            <div className="d-flex justify-content-center">
              <a href="https://www.facebook.com"><FontAwesomeIcon icon={faFacebook} className="me-3 text-light" size="2x"/></a>
              <a href="https://www.twitter.com"><FontAwesomeIcon icon={faTwitter} className="me-3 text-light" size="2x"/></a>
              <a href="https://www.instagram.com"><FontAwesomeIcon icon={faInstagram} className="me-3 text-light" size="2x"/></a>
              <a href="https://www.linkedin.com"><FontAwesomeIcon icon={faLinkedin} className="me-3 text-light" size="2x"/></a>
            </div>
          </Col>
        </Row>
        <Row className="justify-content-center">
          <Col xs={12} className="text-center mt-4">
            <p className="mb-0">&copy; Apna Blog 2024</p>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Footer;
