import React, { useEffect, useState } from "react";
import { Container, Row } from "react-bootstrap";
import PostThumbnailsList from "../Components/PostThumbnailsList";
import Loading from "../Components/Loading";
import Footer from "../Components/Footer/Footer"; // Import the Footer component
import { getAllPosts } from "../lib/api";

const MainPage = (props) => {
  const [posts, setPosts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      getAllPosts().then((data) => {
        setPosts(data);
        setIsLoading(false);
      });
    }, 1000);
  }, []);

  return (
    <Container>
      <Row
        xs={1}
        md={2}
        lg={3}
        className="g-4 pb-4 justify-content-center"
        style={{
          marginTop: props.headerHeight,
          marginBottom: props.footerHeight,
        }}
      >
        {isLoading && <Loading />}
        {!isLoading && <PostThumbnailsList posts={posts} />}
      </Row>
      {!isLoading && <Footer />} {/* Render the footer after all posts have been loaded */}

    </Container>
  );
};

export default MainPage;
