import React, { useContext, useEffect, useRef, useState } from "react";
import { Card, Col, Form } from "react-bootstrap";
import SubmitButton from "../SubmitButton";
import AuthContext from "../../store/auth-context";
import { Formik } from "formik";
import * as Yup from "yup";
import { useTranslation } from "react-i18next";

const NewPostForm = ({ onSubmit, isSubmitted }) => {
  const textAreaRef = useRef(null);
  const authCtx = useContext(AuthContext);
  const { t } = useTranslation();
  const [content, setContent] = useState("");

  useEffect(() => {
    textAreaRef.current.style.height = "300px";
  }, []);

  const handleInputChange = (event) => {
    setContent(event.target.value);
  };

  const applyFormatting = (tag) => {
    const selection = textAreaRef.current.selectionStart;
    const start = content.substring(0, selection);
    const end = content.substring(selection);
    setContent(start + `<${tag}>` + end + `</${tag}>`);
    textAreaRef.current.focus();
    textAreaRef.current.setSelectionRange(selection + 3 + tag.length, selection + 3 + tag.length);
  };

  const handleSubmit = (values, actions) => {
    onSubmit({ ...values, content });
    actions.setSubmitting(false);
  };

  return (
    <Formik
      initialValues={{
        title: "",
      }}
      validationSchema={Yup.object({
        title: Yup.string()
          .min(3, t("validation:atLeast", { number: 3 }))
          .max(30, t("validation:lessThan", { number: 30 }))
          .required(t("validation:required")),
      })}
      onSubmit={handleSubmit}
    >
      {(formik) => (
        <Col>
          <Card className="boxShadow">
            <Card.Header className="fw-bold text-dark">{`${t("newPostBy")} ${
              authCtx.userName
            }`}</Card.Header>
            <Card.Body className="m-2 pb-3 border rounded-3">
              <Form onSubmit={formik.handleSubmit}>
                <Form.Group controlId="title">
                  <Form.Label className="fw-bold text-dark mb-1">{t("title")}</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder={t("enterNewPostHere")}
                    name="title"
                    onChange={formik.handleChange}
                    onBlur={formik.handleBlur}
                    value={formik.values.title}
                    disabled={isSubmitted}
                    isValid={
                      formik.touched.title &&
                      !formik.errors.title &&
                      formik.values.title
                    }
                    isInvalid={formik.touched.title && formik.errors.title}
                  />
                  <Form.Control.Feedback type="invalid">
                    {formik.errors.title}
                  </Form.Control.Feedback>
                </Form.Group>

                <Form.Group className="my-2" controlId="content">
                  <Form.Label className="fw-bold text-dark mb-1">{t("content")}</Form.Label>
                  <div className="formatting-buttons">
                    <button type="button" onClick={() => applyFormatting("b")}><b>B</b></button>
                    <button type="button" onClick={() => applyFormatting("i")}><i>I</i></button>
                    <button type="button" onClick={() => applyFormatting("u")}><u>U</u></button>
                    {/* You can add more formatting options here */}
                  </div>
                  <textarea
                    ref={textAreaRef}
                    onChange={handleInputChange}
                    value={content}
                    disabled={isSubmitted}
                    className="form-control"
                  />
                </Form.Group>
                <div className="d-flex justify-content-end">
                  <SubmitButton
                    isSubmitted={isSubmitted}
                    isSubmitting={formik.isSubmitting}
                    name={t("addNewPost")}
                  />
                </div>
              </Form>
            </Card.Body>
          </Card>
        </Col>
      )}
    </Formik>
  );
};

export default NewPostForm;
