import React from "react";
import { Col, Container, Row } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFacebook, faTwitter, faInstagram, faLinkedin } from "@fortawesome/free-brands-svg-icons";

class Footer extends React.Component {
  render() {
    return (
      <Container
        fluid
        className="bg-dark.bg-gradient text-dark py-4 shadow-sm"
        style={{ borderTop: "12px solid gray" }}

        >
        <Row className="justify-content-center">
          <Col xs={12} className="text-center mb-3">
            <h3>Follow Us</h3>
            <div className="d-flex justify-content-center">
              <a href="https://www.facebook.com"><FontAwesomeIcon icon={faFacebook} className="me-3 fs-3" /></a>
              <a href="https://www.twitter.com"><FontAwesomeIcon icon={faTwitter} className="me-3 fs-3" /></a>
              <a href="https://www.instagram.com"><FontAwesomeIcon icon={faInstagram} className="me-3 fs-3" /></a>
              <a href="https://www.linkedin.com"><FontAwesomeIcon icon={faLinkedin} className="me-3 fs-3" /></a>
            </div>
          </Col>
        </Row>
        <Row className="justify-content-center">
          <Col xs={12} className="text-center">
            <p className="mb-1">&copy; 2024 Apna Blog. All Rights Reserved.</p>
            <p className="mb-1"><a href="/privacy-policy" className="text-light">Privacy Policy</a> | <a href="/terms-of-service" className="text-light">Terms of Service</a></p>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Footer;
